const errorData = $json;

console.log('ì‹œìŠ¤í…œ ì—ëŸ¬ ì•Œë¦¼ ìˆ˜ì‹ :', errorData.error_type);

// ì—ëŸ¬ ì‹¬ê°ë„ ë¶„ë¥˜
function getErrorSeverity(errorType, errorMessage) {
  const criticalKeywords = ['database', 'connection', 'authentication', 'permission', 'crash', 'fatal'];
  const highKeywords = ['api', 'timeout', 'rate limit', 'memory', 'disk space'];

  const lowerError = (errorMessage || '').toLowerCase();
  const lowerType = (errorType || '').toLowerCase();

  if (criticalKeywords.some(keyword => lowerError.includes(keyword) || lowerType.includes(keyword))) {
    return 'CRITICAL';
  } else if (highKeywords.some(keyword => lowerError.includes(keyword) || lowerType.includes(keyword))) {
    return 'HIGH';
  } else {
    return 'MEDIUM';
  }
}

// ì—ëŸ¬ ë¶„ë¥˜
const severity = getErrorSeverity(errorData.error_type, errorData.error_message);

// ì—ëŸ¬ ì •ë³´ êµ¬ì¡°í™”
const errorInfo = {
  errorId: errorData.error_id || `ERR-${Date.now()}`,
  errorType: errorData.error_type || 'Unknown',
  errorMessage: errorData.error_message || 'No message provided',
  severity: severity,
  component: errorData.component || 'Unknown Component',
  timestamp: errorData.timestamp || new Date().toISOString(),
  stackTrace: errorData.stack_trace || null,
  userId: errorData.user_id || null,
  requestId: errorData.request_id || null,
  environment: errorData.environment || 'production',
  additionalData: errorData.additional_data || {},
  dashboardUrl: errorData.dashboard_url || `${$env.DASHBOARD_BASE_URL}/system/errors`
};

// ìš°ì„ ìˆœìœ„ ì„¤ì • (CRITICAL > HIGH > MEDIUM)
const shouldSendEmail = ['CRITICAL', 'HIGH'].includes(severity);

return [{
  json: {
    ...errorInfo,
    shouldSendEmail: shouldSendEmail,
    priorityLevel: severity === 'CRITICAL' ? 1 : severity === 'HIGH' ? 2 : 3
  }
}];

