// ì—ëŸ¬ ë©”ì‹œì§€ ë¶„ì„
const errorMessage = errorInfo.message || '';
const errorType = errorInfo.type || 'unknown';

// ì¬ì‹œë„ ê°€ëŠ¥í•œ ì—ëŸ¬ ìœ í˜• íŒë‹¨
if (errorMessage.includes('timeout') || 
    errorMessage.includes('network') ||
    errorMessage.includes('connection') ||
    errorMessage.includes('rate limit')) {
  severity = 'medium';
  shouldRetry = true;
} else if (errorMessage.includes('authentication') ||
          errorMessage.includes('permission') ||
          errorMessage.includes('not found')) {
  severity = 'high';
  shouldRetry = false;
} else if (errorMessage.includes('syntax') ||
          errorMessage.includes('invalid') ||
          errorMessage.includes('malformed')) {
  severity = 'high';
  shouldRetry = false;
}

// ì›Œí¬í”Œë¡œìš° ì •ì  ë°ì´í„°ì—ì„œ ì¬ì‹œë„ íšŸìˆ˜ í™•ì¸
const staticData = $getWorkflowStaticData('global');
const retryKey = `retry_${executionInfo.id}`;
retryCount = staticData[retryKey] || 0;

// ìµœëŒ€ ì¬ì‹œë„ íšŸìˆ˜ (3íšŒ)
const maxRetries = 3;
if (retryCount >= maxRetries) {
  shouldRetry = false;
  severity = 'critical';
}

const processedError = {
  ...errorData,
  analysis: {
    severity: severity,
    shouldRetry: shouldRetry,
    retryCount: retryCount,
    maxRetries: maxRetries,
    errorCategory: getErrorCategory(errorMessage),
    timestamp: new Date().toISOString()
  }
};

function getErrorCategory(message) {
  const categories = {
    'network': ['timeout', 'connection', 'network', 'dns'],
    'authentication': ['auth', 'permission', 'unauthorized', 'forbidden'],
    'api_limit': ['rate limit', 'quota', 'limit exceeded'],
    'data': ['not found', 'invalid', 'malformed', 'parse'],
    'system': ['memory', 'disk', 'cpu', 'resource']
  };

  for (const [category, keywords] of Object.entries(categories)) {
    if (keywords.some(keyword => message.toLowerCase().includes(keyword))) {
      return category;
    }
  }
  return 'unknown';
}

console.log(`ì—ëŸ¬ ë¶„ì„ ì™„ë£Œ: ${severity} - ì¬ì‹œë„: ${shouldRetry}`);

return [{ json: processedError }];

